package WS1718.PROG1.ES01;
import WS1718.PROG1.prog.utils.TextIO;
public class EscapeCharacters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		TextIO.putln("escape sequence is started with a backslash: \\\n"
				+ "to print a tab (	)	use \"\\t\"\n"
				+ "to print a backslash (\\)	use \"\\\\\"\n"
				+ "to print double quotes (\")	use \"\\\"\"\n"
				+ "to print a single quote (\')	use \"\\\'\"\n"
				+ "to start a new line (\n)				use \"\\n\"");
	}

}
