package WS1718.PROG1.ES01;
import WS1718.PROG1.prog.utils.TextIO;
public class TheLifeOfPi {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		TextIO.putf("Pi = %f!\n", 3.14159265359);
		TextIO.putf("Pi = %12f!\n", 3.14159265359);
		TextIO.putf("Pi = %12.2f!\n", 3.14159265359);
		TextIO.putf("Pi = %10.0f!\n", 3.14159265359);
		TextIO.putf("Pi = %13.11f!\n", 3.14159265359);
		TextIO.putf("Pi = %015f!\n", 3.14159265359);
		TextIO.putf("Pi = %+015.2f!\n", 3.14159265359);
	}
}
